#!/bin/bash\n\n# Test script for new ports (4000 frontend, 9000 backend)\n\necho \"🧪 Testing AI Career Coach on new ports...\"\necho \"\"\n\n# Test backend health\necho \"📡 Testing Backend (Port 9000)...\"\nif curl -f http://localhost:9000/api/health > /dev/null 2>&1; then\n    echo \"✅ Backend API is running on port 9000\"\n    echo \"📋 API Health Response:\"\n    curl -s http://localhost:9000/api/health | python3 -m json.tool 2>/dev/null || echo \"API returned non-JSON response\"\nelse\n    echo \"❌ Backend API is not responding on port 9000\"\n    echo \"💡 Start backend with: cd api && python app.py\"\nfi\n\necho \"\"\n\n# Test frontend\necho \"🌐 Testing Frontend (Port 4000)...\"\nif curl -f http://localhost:4000 > /dev/null 2>&1; then\n    echo \"✅ Frontend is running on port 4000\"\nelse\n    echo \"❌ Frontend is not responding on port 4000\"\n    echo \"💡 Start frontend with: cd frontend && npm run dev\"\nfi\n\necho \"\"\necho \"📋 Service URLs:\"\necho \"   🌐 Frontend: http://localhost:4000\"\necho \"   📡 Backend API: http://localhost:9000\"\necho \"   📚 API Docs: http://localhost:9000/docs\"\necho \"\"\necho \"🚀 Ready to test the AI Career Coach!\"\necho \"\"\necho \"💡 Quick Test Commands:\"\necho \"   # Test basic chat:\"\necho \"   curl -X POST http://localhost:9000/api/chat -H 'Content-Type: application/json' -d '{\\\"user_message\\\":\\\"Hello\\\", \\\"api_key\\\":\\\"YOUR_API_KEY\\\", \\\"system_prompt\\\":\\\"You are helpful\\\"}'\"\necho \"\"\necho \"   # Test career coach:\"\necho \"   curl -X POST http://localhost:9000/api/career-coach -H 'Content-Type: application/json' -d '{\\\"user_message\\\":\\\"What are the top AI jobs?\\\", \\\"api_key\\\":\\\"YOUR_API_KEY\\\"}'\"\necho \"\""